pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Detailed.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Burnable.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";


// 每一期的时间段
library LStageTime {
    struct StageTime {
        // 1. 众筹期
        uint256 saleBeginTime;
        uint256 saleEndTime;

        // 2. 冻结期
        uint256 lockBeginTime;
        uint256 lockEndTime;

        // 3. 投票期
        uint256 voteBeginTime;
        uint256 voteEndTime;
    }

    // 是否在众筹期
    function IsInSale(StageTime storage stageTime, uint256 curTime)
    internal
    view
    returns (bool) {
        return curTime >= stageTime.saleBeginTime && curTime <= stageTime.saleEndTime;
    }

    // 是否在冻结期
    function IsInLock(StageTime storage stageTime, uint256 curTime)
    internal
    view
    returns (bool) {
        return curTime >= stageTime.lockBeginTime && curTime <= stageTime.lockEndTime;
    }

    // 是否在投票期
    function IsInVote(StageTime storage stageTime, uint256 curTime)
    internal
    view
    returns (bool) {
        return curTime >= stageTime.voteBeginTime && curTime <= stageTime.voteEndTime;
    }

}

contract StagesToken is ERC20, ERC20Detailed, ERC20Burnable {

    using SafeMath for uint;

    address public owner;

    constructor (
        string name,
        string symbol,
        uint8 decimals,
        uint256 totalSupply
    )
    ERC20Burnable()
    ERC20Detailed(name, symbol, decimals)
    ERC20()
    public
    {
        _totalSupply = totalSupply * (10 ** uint256(decimals));

        owner = msg.sender;
        _balances[owner] = _totalSupply;
    }
}
